import React from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik'
import * as Yup from 'yup'
import axios from 'axios'

function CreatePost() {

    const initialValues = {
        title: "",
        postText: "",
        username: ""
    };

    const validationSchema = Yup.object().shape({
        title: Yup.string().required("A title can not be blank!"),
        postText: Yup.string(),
        username: Yup.string().min(3).max(15).required()
    });

    const onSubmit = (data) => {
        axios.post("http://localhost:3001/posts", data)
        .then( (response) => {
            console.log("Post Created.");
        });
    };

    return (
        <div className="create-post-page">
            <Formik initialValues={initialValues} onSumbit={onSubmit} validationSchema={validationSchema} >
                <Form className="form-container">
                    <label>Title:</label>
                    <ErrorMessage name="title" component="span" />
                    <Field id="input-create-post" name="title" placeholder="Title" />
                    <label>Post:</label>
                    <ErrorMessage name="postText" component="span" />
                    <Field id="input-create-post" name="postText" placeholder="Post Text" />
                    <label>Username:</label>
                    <ErrorMessage name="username" component="span" />
                    <Field id="input-create-post" name="username" placeholder="Username" />

                    <button type="submit">Create Post</button>
                </Form>
            </Formik>
        </div>
    )
};

export default CreatePost;
